
var init;var Graf_NR=0;$.ajaxSetup({cache:false});var AjaxReciveTime;var AjaxSendTime;var html_txt="";function GetData(){if((Graf_NR>=18)&&(Graf_NR<21)){Graf_NR++}else{if(Graf_NR===21){for(NR_X=18;NR_X>0;NR_X--){$(".NR"+NR_X).css({height:"0px","transition-duration":"0s"})}console.log("Reseting graf stage 1");Graf_NR++;$(".connection .status").html('<span class="green">Reseting graf...</span>')}else{if(Graf_NR===22){for(NR_X=18;NR_X>0;NR_X--){$(".NR"+NR_X).css({"transition-duration":"0.5s"})}console.log("Reseting graf stage 2");Graf_NR++}else{if(Graf_NR===23){Graf_NR=-1;console.log("Reseting graf stage 3");Graf_NR++;html_txt=""}else{var a=new Date();AjaxSendTime=a.getTime();$.ajax({method:"GET",url:"data1.php",dataType:"text",success:function(b){if(Graf_NR<18){console.log("connection success");var c=b;var e=Math.round(b*2.575)+"px";Graf_NR++;console.log("Graf_Height:"+e+" Graf_NR:"+Graf_NR);$(".NR"+Graf_NR).css("height",e);var d=new Date();AjaxReciveTime=d.getTime();var f=AjaxReciveTime-AjaxSendTime;var g="";console.log("time:"+f);if(f<100){g='<span class="green">'+f+"</span>"}if(f>=100){g='<span class="yelow">'+f+"</span>"}if(f>=500){g='<span class="orange">'+f+"</span>"}if(f>=1000){g='<span class="red">'+f+"</span>"}html_txt=html_txt+('<div class="line"><div class="box left">Online reciving data: [ nr: <b>'+Graf_NR+"</b> ] [ data: <b>"+c+'</b> ]</div> <div class="box right">Server response time: [ '+(g)+"ms ]</div></div>");$(".connection .status").html(html_txt)}},error:function(){$(".connection .status").html('<span class="red"><b>Waiting for server...</b></span>')}})}}}}}$(document).ready(function(){var a=setInterval(GetData,2000)});